<template>
<div class="alphbet-classify">
  <div class="alphas-wrap">
    <div
      v-for="(item, index) in letterArrHasData"
      class="single-alpha"
      :class="{'alpha-show': item.show}"
      @click="hoverFn(index)"
      :key="index">{{item.alpha}}
    </div>
  </div>
  <div class="alphaData-wrap">
    <el-checkbox-group v-model="checkedList" class="check-group-wrap">
      <el-checkbox v-for="item in alphaDataArr" :label="item.key"></el-checkbox>
    </el-checkbox-group>
  </div>
</div>
</template>

<script>
/*
 * <alphabet-classify
      :originData="carTypeArr"
      :checkedData="carTypeList"
      @checkedChange="checkedCarTypeChanged"
    >
    </alphabet-classify>
    :originData 传入全部数据
    :checkedData 传入已选中数据
    @checkedChange 绑定选中改变时的函数，参数为选中数据数组。
 */
export default {
  name: 'AlphabetClassify',
  props: ['originData', 'checkedData'],
  data () {
    return {
      checkedList: [],
      alphaDataArr: [],
      letterArr: [{
        'alpha': 'A',
        'alphaData': [],
        'show': true
      }, {
        'alpha': 'B',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'C',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'D',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'E',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'F',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'G',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'H',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'I',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'J',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'K',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'L',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'M',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'N',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'O',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'P',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'Q',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'R',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'S',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'T',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'U',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'V',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'W',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'X',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'Y',
        'alphaData': [],
        'show': false
      }, {
        'alpha': 'Z',
        'alphaData': [],
        'show': false
      }],
      something: ''
    }
  },
  computed: {
    letterArrHasData () {
      return this.letterArr.filter((singleLetter) => {
        if (singleLetter.alphaData.length === 0) {
          return false
        } else {
          return true
        }
      })
    }
  },
  watch: {
    originData () {
      this.init()
    },
    checkedData () {
      this.checkedList = this.checkedData
    },
    checkedList () {
      this.$emit('checkedChange', this.checkedList)
    }
  },
  methods: {
    init () {
      // 先清空，防止切换省份的时候累加push，已选中的也清空
      this.letterArr.forEach((item) => {
        item.alphaData = []
      })
      this.checkedList = []
      for (let i = 0; i < this.originData.length; i++) {
        let word = this.originData[i].value
        let firstAlpha = ''
        if (word === '重庆市' || word === '重庆') {
          firstAlpha = 'C'
        } else {
          firstAlpha = this.getSpell(word[0])
        }
        for (let j = 0; j < this.letterArr.length; j++) {
          if (this.letterArr[j].alpha === firstAlpha) {
            this.letterArr[j].alphaData.push(this.originData[i])
            break
          }
        }
      }
      this.alphaDataArr = this.letterArr[0].alphaData
      this.hoverFn(0)
    },
    hoverFn (index) {
      for (let i = 0; i < this.letterArrHasData.length; i++) {
        if (i !== index) {
          this.letterArrHasData[i].show = false
        } else {
          this.letterArrHasData[i].show = true
          this.alphaDataArr = this.letterArrHasData[i].alphaData
        }
      }
    },
    getSpell (word) {
      // 汉字拼音首字母列表 本列表包含了20902个汉字,用于配合 ToChineseSpell
      // 函数使用,本表收录的字符的Unicode编码范围为19968至40869, XDesigner 整理
      let strChineseFirstPY = ''

      function makePy (str) {
        if (typeof str !== 'string') throw new Error(-1, '函数makePy需要字符串类型参数!')
        var arrResult = []
        // 保存中间结果的数组
        for (var i = 0, len = str.length; i < len; i++) {
          // 获得unicode码
          var ch = str.charAt(i)
          // 检查该unicode码是否在处理范围之内,在则返回该码对映汉字的拼音首字母,不在则调用其它函数处理
          arrResult.push(checkCh(ch))
        }
        // 处理arrResult,返回所有可能的拼音首字母串数组
        return mkRslt(arrResult)
      }

      function checkCh (ch) {
        var uni = ch.charCodeAt(0)
        // 如果不在汉字处理范围之内,返回原字符,也可以调用自己的处理函数
        if (uni > 40869 || uni < 19968) return ch
        // dealWithOthers(ch)
        // 检查是否是多音字,是按多音字处理,不是就直接在strChineseFirstPY字符串中找对应的首字母
        return (strChineseFirstPY.charAt(uni - 19968))
      }

      function mkRslt (arr) {
        var arrRslt = ['']
        for (var i = 0, len = arr.length; i < len; i++) {
          var str = arr[i]
          var strlen = str.length
          if (strlen === 1) {
            for (var k = 0; k < arrRslt.length; k++) {
              arrRslt[k] += str
            }
          } else {
            var tmpArr = arrRslt.slice(0)
            arrRslt = []
            for (k = 0; k < strlen; k++) {
              // 复制一个相同的arrRslt
              var tmp = tmpArr.slice(0)
              // 把当前字符str[k]添加到每个元素末尾
              for (var j = 0; j < tmp.length; j++) {
                tmp[j] += str.charAt(k)
              }
              // 把复制并修改后的数组连接到arrRslt上
              arrRslt = arrRslt.concat(tmp)
            }
          }
        }
        // 去除前后空格
        arrRslt = arrRslt.toString().replace(/(^\s*)|(\s*$)/g, '')
        // 去除所有空格
        // arrRslt = arrRslt.toString().replace(/\s+/g, '')
        return arrRslt
      }

      return makePy(word)
    }
  }
}
</script>

<style scoped>
.alphas-wrap {
  white-space: nowrap;
  text-align: left;
  padding-left: 20px;
  padding-top: 5px;
}
.single-alpha {
  display: inline-block;
  width: 22px;
  font-weight: bold;
  font-size: 14px;
  line-height: 18px;
  color: #409EFF;
  margin: 0 5px;
  text-align: center;
}
.alphas-wrap .alpha-show {
  color: #fff;
  background-color: #409EFF;
  position: relative;
}
.alpha-show:after {
  content: '';
  position: absolute;
  top: 19px;
  left: 5px;
  width: 12px;
  height: 7px;
  background: url(../../../static/arrow.png) no-repeat;
  z-index: 1;
}
.alphaData-wrap {
  margin: 4px 20px 0;
  border: 1px dashed #ccd3e4;
  position: relative;
  z-index: auto;
